import {
  React,
  theme,
  calendarStyles,
  moment,
  ThemeProvider,
  Button,
} from "./index.js";

const isSelected = (day, value) => {
  return value.isSame(day, "day");
};

const beforeToday = (day) => {
  return day.isBefore(new Date(), "day");
};

const isToday = (day) => {
  return day.isSame(new Date(), "day");
};

const DayButton = (day) => {
  const classes = calendarStyles();
  console.log(new Date(), day);
  const dayStyles = (day, value) => {
    if (beforeToday(day)) return classes.beforeDayButton;
    if (isSelected(day, value)) return classes.selectedDayButton;
    if (isToday(day)) return classes.todayDayButton;
    return "";
  };

  return (
    <div>
      <ThemeProvider theme={theme}>
        <Button className={dayStyles}>{day.day}</Button>
      </ThemeProvider>
    </div>
  );
};

export default DayButton;
