import { makeStyles } from "@mui/styles";
import { createTheme } from "@mui/material/styles";

const theme = createTheme();

const buttonStyles = makeStyles((theme) => ({
  selectedCalendarButton: {
    color: "grey",
    borderRadius: 50,
    height: "100px",
    width: "100px",
    margin: 100,
  },

  beforeCalendarButton: {},

  todayCalendarButton: {},
}));

const isSelected = (day, value) => {
  return value.isSame(day, "day");
};

const beforeToday = (day) => {
  return day.isBefore(new Date(), "day");
};

const isToday = (day) => {
  return day.isSame(new Date(), "day");
};

export const dayStyles = (day, value) => {
  if (beforeToday(day)) return "beforeCalendarButton";
  if (isSelected(day, value)) return "selectedCalendarButton";
  if (isToday(day)) return "todayCalendarButton";
  return "";
};

export default buttonStyles;
