import { calendarStyles } from "./index.js";

const classes = calendarStyles();

const dayStyles = (date) => {
  const todaysDate = new Date().getDate();

  if (date < todaysDate) return classes.beforeDayButton;
  if (date == todaysDate) return classes.selectedDayButton;
  if (date > todaysDate) return classes.todayDayButton;
  return "";
};

export default dayStyles;
