import {
  React,
  moment,
  useState,
  useEffect,
  cssStyles,
  buildCalendar,
  dayStyles,
  Header,
  styled,
  Button,
} from "./index";

import { makeStyles } from "@mui/styles";
import { createTheme, ThemeProvider } from "@mui/material/styles";

// const theme = createTheme();

// const MUIstyles = makeStyles((theme) => ({
//   calendarButton: {
//     color: "grey",
//     borderRadius: 50,
//     height: "100px",
//     width: "100px",
//     margin: 100,
//   },
// }));

const Calendar = () => {
  const [calendar, setCalendar] = useState([]);
  const [value, setValue] = useState(moment());

  useEffect(() => {
    setCalendar(buildCalendar(value));
  }, [value]);

  return (
    <ThemeProvider theme={theme} className="Calendar">
      <div>
        <Header value={value} setValue={setValue} />
        <div className="Body">
          <div className="Day-Names">
            {["s", "m", "t", "w", "t", "f", "s"].map((d) => (
              <div className="week">{d}</div>
            ))}
          </div>
          {calendar.map((week) => (
            <div>
              {week.map((day) => (
                <div className="Day" onClick={() => setValue(day)}>
                  <Button className={dayStyles(day, value)}>
                    {day.format("D")}
                  </Button>
                </div>
              ))}
            </div>
          ))}
        </div>
      </div>
    </ThemeProvider>
  );
};

export default Calendar;
