import {
  React,
  moment,
  useState,
  useEffect,
  styles,
  buildCalendar,
  dayStyles,
  Header,
  styled,
} from "./index";

const CalendarButton = styled("button")({
  color: "grey",
  borderRadius: 50,
  height: "100px",
  width: "100px",
});

const Calendar = () => {
  const [calendar, setCalendar] = useState([]);
  const [value, setValue] = useState(moment());

  useEffect(() => {
    setCalendar(buildCalendar(value));
  }, [value]);

  return (
    <div className="Calendar">
      <Header value={value} setValue={setValue} />
      <div className="Body">
        <div className="Day-Names">
          {["s", "m", "t", "w", "t", "f", "s"].map((d) => (
            <div className="week">{d}</div>
          ))}
        </div>
        {calendar.map((week) => (
          <div>
            {week.map((day) => (
              <div className="Day" onClick={() => setValue(day)}>
                <CalendarButton className={dayStyles(day, value)}>
                  {day.format("D")}
                </CalendarButton>
              </div>
            ))}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Calendar;
